INSERT INTO `t_post` VALUES (18, 5, 0, 'thundernet8', 1, 'k78807844', 9, '207.226.143.125', 'Mozilla/5.0 (Macintosh; Intel Mac OS X 10_12_6) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/61.0.3163.100 Safari/537.36', '/**\n * Class SlideVM\n */\nclass SlideVM extends BaseVM {\n    protected function __construct() {\n        $this->_cacheUpdateFrequency = \'daily\';\n        $this->_cacheInterval = 3600*24;\n    }\n\n    protected function getRealData() {\n        $slide_postIds = explode(\',\', tt_get_option(\'tt_home_slides\'));\n\n        if(!count($slide_postIds)) {\n            return null;\n        }\n\n        $args = array(\n            \'post_type\' => \'post\',\n            \'post_status\' => \'publish\',\n            \'posts_per_page\' => -1,\n            \'post__in\' => $slide_postIds,\n            \'has_password\' => false,\n            \'ignore_sticky_posts\' => true,\n            \'orderby\' => \'none\'\n        );\n\n        $query = new WP_Query($args);\n\n        $slide_posts = array();\n\n        while ($query->have_posts()) : $query->the_post();\n            $slide_post = array();\n            global $post;\n            $slide_post[\'title\'] = get_the_title($post);\n            $slide_post[\'permalink\'] = get_permalink($post);\n            $slide_post[\'comment_count\'] = $post->comment_count;\n            $slide_post[\'category\'] = get_the_category_list(\' · \', \'\', $post->ID);\n            $slide_post[\'author\'] = get_the_author(); //TODO add link\n            $slide_post[\'author_url\'] = home_url(\'/@\' . $slide_post[\'author\']);\n            $slide_post[\'time\'] = get_post_time(\'F j, Y\', false, $post, false); //get_post_time( string $d = \'U\', bool $gmt = false, int|WP_Post $post = null, bool $translate = false )\n            $slide_post[\'datetime\'] = get_the_time(DATE_W3C, $post);\n            $slide_post[\'thumb\'] = tt_get_thumb($post, array(\n                \'width\' => 750,\n                \'height\' => 375,\n                \'str\' => \'large\'\n            ));\n\n            $slide_posts[strval($post->ID)] = $slide_post;\n        endwhile;\n\n        wp_reset_postdata();\n\n        $ordered_posts = array();\n\n        foreach ($slide_postIds as $value) {\n            $ordered_posts[] = $slide_posts[$value];\n        }\n\n        return $ordered_posts;\n    }\n}', '<pre class=\"draft2html--pre\"><code class=\"_lang_default draft2html--code\"><span class=\"token comment\" spellcheck=\"true\">/**\n* Class SlideVM\n*/</span>\nclass <span class=\"token class-name\">SlideVM</span> extends <span class=\"token class-name\">BaseVM</span> <span class=\"token punctuation\">{</span>\n   protected <span class=\"token keyword\">function</span> <span class=\"token function\">__construct</span><span class=\"token punctuation\">(</span><span class=\"token punctuation\">)</span> <span class=\"token punctuation\">{</span>\n       $this<span class=\"token operator\">-</span><span class=\"token operator\">></span>_cacheUpdateFrequency <span class=\"token operator\">=</span> <span class=\"token string\">\'daily\'</span><span class=\"token punctuation\">;</span>\n       $this<span class=\"token operator\">-</span><span class=\"token operator\">></span>_cacheInterval <span class=\"token operator\">=</span> <span class=\"token number\">3600</span><span class=\"token operator\">*</span><span class=\"token number\">24</span><span class=\"token punctuation\">;</span>\n   <span class=\"token punctuation\">}</span>\n\n   protected <span class=\"token keyword\">function</span> <span class=\"token function\">getRealData</span><span class=\"token punctuation\">(</span><span class=\"token punctuation\">)</span> <span class=\"token punctuation\">{</span>\n       $slide_postIds <span class=\"token operator\">=</span> <span class=\"token function\">explode</span><span class=\"token punctuation\">(</span><span class=\"token string\">\',\'</span><span class=\"token punctuation\">,</span> <span class=\"token function\">t