nction\">wp_reset_postdata</span><span class=\"token punctuation\">(</span><span class=\"token punctuation\">)</span><span class=\"token punctuation\">;</span>\n\n        $ordered_posts <span class=\"token operator\">=</span> <span class=\"token function\">array</span><span class=\"token punctuation\">(</span><span class=\"token punctuation\">)</span><span class=\"token punctuation\">;</span>\n\n        foreach <span class=\"token punctuation\">(</span>$slide_postIds as $value<span class=\"token punctuation\">)</span> <span class=\"token punctuation\">{</span>\n            $ordered_posts<span class=\"token punctuation\">[</span><span class=\"token punctuation\">]</span> <span class=\"token operator\">=</span> $slide_posts<span class=\"token punctuation\">[</span>$value<span class=\"token punctuation\">]</span><span class=\"token punctuation\">;</span>\n        <span class=\"token punctuation\">}</span>\n\n        <span class=\"token keyword\">return</span> $ordered_posts<span class=\"token punctuation\">;</span>\n    <span class=\"token punctuation\">}</span>\n<span class=\"token punctuation\">}</span></code></pre>\n', '{\"entityMap\":{\"0\":{\"type\":\"MENTION\",\"mutability\":\"IMMUTABLE\",\"data\":{\"text\":\"@k78807844\",\"value\":\"k78807844\",\"url\":\"javascript:;\"}}},\"blocks\":[{\"key\":\"avgd9\",\"text\":\"@k78807844 \",\"type\":\"unstyled\",\"depth\":0,\"inlineStyleRanges\":[],\"entityRanges\":[{\"offset\":0,\"length\":10,\"key\":0}],\"data\":{}},{\"key\":\"e3401\",\"text\":\"上面发的代码是没修改过的，这个是修改之后的\",\"type\":\"unstyled\",\"depth\":0,\"inlineStyleRanges\":[],\"entityRanges\":[],\"data\":{}},{\"key\":\"adj30\",\"text\":\"\",\"type\":\"unstyled\",\"depth\":0,\"inlineStyleRanges\":[],\"entityRanges\":[],\"data\":{}},{\"key\":\"48tsk\",\"text\":\"/**\\n * Class SlideVM\\n */\\nclass SlideVM extends BaseVM {\\n    protected function __construct() {\\n        $this->_cacheUpdateFrequency = \'daily\';\\n        $this->_cacheInterval = 3600*24;\\n    }\\n\\n    protected function getRealData() {\\n        $slide_postIds = explode(\',\', tt_get_option(\'tt_home_slides\'));\\n\\n        if(!count($slide_postIds)) {\\n            return null;\\n        }\\n\\n        $args = array(\\n            \'post_type\' => \'post\',\\n            \'post_status\' => \'publish\',\\n            \'posts_per_page\' => -1,\\n            \'post__in\' => $slide_postIds,\\n            \'has_password\' => false,\\n            \'ignore_sticky_posts\' => true,\\n            \'orderby\' => \'none\'\\n        );\\n\\n        $query = new WP_Query($args);\\n\\n        $slide_posts = array();\\n\\n        while ($query->have_posts()) : $query->the_post();\\n            $slide_post = array();\\n            global $post;\\n            $slide_post[\'title\'] = get_the_title($post);\\n            $slide_post[\'permalink\'] = get_permalink($post);\\n            $slide_post[\'comment_count\'] = $post->comment_count;\\n            $slide_post[\'category\'] = get_the_category_list(\' · \', \'\', $post->ID);\\n            $slide_post[\'author\'] = get_the_author(); //TODO add link\\n            $slide_post[\'author_url\'] = home_url(\'/@\' . $slide_post[\'author\']);\\n            $slide_post[\'time\'] = get_post_time(\'F j, Y\', false, $post, false); //get_post_time( string $d = \'U\', bool $gmt = false, int|WP_Post $post = null, bool $translate = false )\\n            $slide_post[\'datetime\'] = get_the_time(DATE_W3C, $post);\\n            $slide_post[\'thumb\'] = tt_get_thumb($post, array(\\n                \'width\' => 750,\\n                \'height\' => 375,\\n                \'str\' => \'large\'\\n            ));\\n\\n            $slide_posts[strval($post->ID)] = $slide_post;\\n        endwhile;\\n\\n        wp_reset_postdata();\\n\\n        $ordered_posts = array();\\n\\n        foreach ($slide_postIds as $value) {\\n            $ordered_posts[] = $slide_posts[$value];\\n        }\\n\\n        return $ordered_posts;\\n    }\\n}\",\"type\":\"code\",\"depth\":0,\"inlineStyleRanges\":[],\"entityRanges\":[],\"data\":{}}]}', 0, 0, 1, 1, 1508438509);
